import React, { Component } from 'react';
import { Grid, Table, Button, Form, Header, Icon, Menu, Modal, Sidebar, Container, Card } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import Cookies from 'js-cookie';
import {Router} from '../../routes';
import web3 from '../../Ethereum/web3';
import Election from '../../Ethereum/election';
import ReqTable from '../../components/ReqTable';

let table1 = [                
    <tr>
        <th>Sr. No.</th>
        <th>E-mail ID: </th>
        <th/>
    </tr>                   
];
class VotingList extends Component { 

    state = {
        election_address: Cookies.get('address'),
        election_name: '',
        election_description: '',
        emailArr: [],
        idArr: [] 
    }

    async componentDidMount() {
        var http = new XMLHttpRequest();
        var url = '/voter/';        
        http.open("GET", url, true);
        let email=[];
        let id=[]
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") {
                  for (let voter of responseObj.data.voters) {
                        email.push(voter.email);
                        id.push(voter.id);    
                  } 
                }                
            }
        };
        http.send();
        this.state.emailArr.push(email);
        console.log(this.state.emailArr);
        this.state.idArr.push(id);

        try {
            const add = Cookies.get('address');
            const election = Election(add);
            const summary = await election.methods.getElectionDetails().call();
            this.setState({
                election_name: summary[0],
                election_description: summary[1]
            });

        } catch(err) {
            console.log(err.message);
            alert("Redirecting you to login page...");
            Router.pushRoute('/company_login');
        }
        let ea = [];
        ea = this.state.emailArr[0];
        let ia = this.state.idArr[0];   
        console.log(ea);         
        let table2 = []            
           
        for(let i=0; i<ea.length; i++) 
        {   
            table2.push(
                <tr>
                    <td>{`${i}`}</td>  
                    <td>{`${ea[i]}`}</td>
                    <td id={`${ia[i]}`}/>
                </tr>
                )
            table1.push(table2);
            console.log(ea[i]);
        }
    }

    renderTable = () => {
        if(this.state.emailArr == null) 
            return (<h1>No voters defined. Please register the voters at your right.</h1>)
        else {              
            //console.log(table1);   
            return (table1);
        }
    } 

  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (

    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='user outline' />
          Candidate List
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='list' />
          Voter List
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='sign out' />
          Sign Out
          </Menu.Item>

      </Sidebar>
    </Sidebar.Pushable>
  )
  CardExampleGroupProps = () => (<Card.Group >

  </Card.Group>)

    register = event => {

		const email = document.getElementById('register_voter_email').value;
    //const election_address = 'abc'; //change to state variable
    
		var http = new XMLHttpRequest();
        var url = "/voter/register";
        var params = "email=" + email+"&election_address=" + this.state.election_address+ "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") {
                  alert(responseObj.message);
                  //window.location("localhost:3000");
                }
                else {
                  alert(responseObj.message);
                }
            }
        };
    	http.send(params);
	}
	
	mail_to_all = event => {
		//const election_address = 'abc'; //change to state variable
		//const election_name = 'e_name';
		//const election_description = 'e_desc';
		var http = new XMLHttpRequest();
        var url = "voter/sendMail";
        var params = "election_address=" + this.state.election_address + "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") {
                  alert(responseObj.message);
                  //window.location("localhost:3000");
                }
                else {
                  alert(responseObj.message);
                }
            }
        };
    	http.send(params);
	}

  renderRows() {
      return(
          this.state
      )
  }

  render() {
      const {Body, Row, HeaderCell, Header} = Table;
    return (
      <div>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Layout>                      
              <br />
              <br />
              <Grid.Column width={14} style={{ minHeight: '630px' }}>
                <Grid.Column style={{ float: 'left', width: '60%' }}>
                  <Header as='h2' color='black'>
                    Voter List
              </Header>
                  <Container>                      
                      <table>
                      {this.renderTable()}
                      </table>                                        
                  </Container>
                </Grid.Column>
                <Grid.Column style={{ float: 'right', width: '30%' }}>
                  <Container style={{}}>
                    <Header as='h2' color='black'>
                      Register Voter
                       </Header>
                    <Card style={{ width: '100%' }}>
                      <br/>
                      <Form.Group size='large' style={{ marginLeft: '15%', marginRight: '15%' }} >
                        <Form.Input
						style={{marginTop: '10px'}}
                          fluid
                          id='register_voter_email'
                          label='Email:'
                          placeholder='Enter your email.'
                          textAlign='center'
                        />

                        <br /><br />
                        <Button primary style={{ Bottom: '10px', marginBottom: '15px' }} onClick={this.register}>Register</Button>
                      </Form.Group>
                    </Card>
                  </Container>
                </Grid.Column>                
              </Grid.Column>
            </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}


export default VotingList
