import React, { Component } from 'react';
import { Grid, Table, Button, Form, Header, Icon, Menu, Modal, Sidebar, Container, Card } from 'semantic-ui-react';
import Layout from '../../components/Layout';
class VotingList extends Component { 

    state = {
        //election_name: '',
        //election_desc: ''
        //whatever values you want to put
        //setting the values: this.setState({election_name: value})
        //getting the values(inside a function): this.state.election_address 
        election_address: 'abc',
        election_name: 'e_name',
        election_description: 'e_desc'
    }

    constructor () {
        super();
        {this.onLoad()}
    }

  onLoad = event => {
    var http = new XMLHttpRequest();
    var url = "voter/";
    http.open("GET", url, true);
    //Send the proper header information along with the request
    http.setRequestHeader(
        "Content-type",
        "application/x-www-form-urlencoded"
    );
    http.onreadystatechange = function() {
        //Call a function when the state changes.
        if (http.readyState == 4 && http.status == 200) {
            var responseObj = JSON.parse(http.responseText);
            if(responseObj.status=="success") {
              for (let voter of responseObj.data.voters) {
                //add code voter.email voter.id
              } 
            }
            
        }
    };
    http.send();
  }  
  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (

    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='user outline' />
          Candidate List
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='list' />
          Voter List
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='sign out' />
          Sign Out
          </Menu.Item>

      </Sidebar>
    </Sidebar.Pushable>
  )
  CardExampleGroupProps = () => (<Card.Group >

  </Card.Group>)

    register = event => {

		const email = document.getElementById('register_voter_email').value;
    //const election_address = 'abc'; //change to state variable
    
		var http = new XMLHttpRequest();
        var url = "voter/register";
        var params = "email=" + email+"&election_address=" + this.state.election_address+ "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") {
                  alert(responseObj.message);
                  //window.location("localhost:3000");
                }
                else {
                  alert(responseObj.message);
                }
            }
        };
    	http.send(params);
	}
	
	mail_to_all = event => {
		//const election_address = 'abc'; //change to state variable
		//const election_name = 'e_name';
		//const election_description = 'e_desc';
		var http = new XMLHttpRequest();
        var url = "voter/sendMail";
        var params = "election_address=" + this.state.election_address + "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") {
                  alert(responseObj.message);
                  //window.location("localhost:3000");
                }
                else {
                  alert(responseObj.message);
                }
            }
        };
    	http.send(params);
	}

  render() {
    return (
      <div>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Layout>
              <br />
              <br />
              <Grid.Column width={14} style={{ minHeight: '630px' }}>
                <Grid.Column style={{ float: 'left', width: '60%' }}>
                  <Header as='h2' color='black'>
                    Voter List
              </Header>
                  <Container >
                    <Table celled compact>
                      <Table.Header>
                        <Table.Row>
                          <Table.HeaderCell collapsing>Sr. No.</Table.HeaderCell>
                          <Table.HeaderCell>E-mail address</Table.HeaderCell>
                          <Table.HeaderCell />
                        </Table.Row>
                      </Table.Header>

                      <Table.Body>
                        <Table.Row>
                          <Table.Cell collapsing>1</Table.Cell>
                          <Table.Cell>abc@abc.co</Table.Cell> {/* voter.email */}
                          <Table.Cell textAlign='center' style={{ width: '50%' }}>                            
                            <Modal size={'tiny'} trigger={<Button positive>Edit</Button>} closeIcon> {/* voter.id */}
                              <Modal.Header>Edit Email ID</Modal.Header> 
                              <center>
                                <Modal.Content>
                                  <Form.Input icon='user' iconPosition='left' placeholder='Username' style={{ padding: 5, textAlign: 'center' }} />
                                  <br />
                                  <Button icon='mail' color="black" padding="10" style={{ marginBottom: '30px' }}>Update</Button>
                                </Modal.Content>
                              </center>
                            </Modal>
                            <Button negative>Delete</Button>
                          </Table.Cell>
                        </Table.Row>
                        <Table.Row>
                          <Table.Cell collapsing>2</Table.Cell>
                          <Table.Cell>abc@abc.co</Table.Cell>
                          <Table.Cell textAlign='center'>                            
                            <Modal size={'tiny'} trigger={<Button positive>Edit</Button>} closeIcon>
                              <Modal.Header>Edit Email ID</Modal.Header>
                              <center>
                                <Modal.Content>
                                  <Form.Input icon='user' iconPosition='left' placeholder='Username' style={{ padding: 5, textAlign: 'center' }} />
                                  <br />
                                  <Button icon='mail' color="black" padding="10" style={{ marginBottom: '30px' }}>Send Mail</Button>
                                </Modal.Content>
                              </center>
                            </Modal>
                            <Button negative>Delete</Button>
                          </Table.Cell>
                        </Table.Row>
                        <Table.Row>
                          <Table.Cell collapsing>3</Table.Cell>
                          <Table.Cell>abc@abc.co</Table.Cell>
                          <Table.Cell textAlign='center'>
                            <Modal size={'tiny'} trigger={<Button positive>Edit</Button>} closeIcon>
                              <Modal.Header>Edit Email ID</Modal.Header>
                              <center>
                                <Modal.Content>
                                  <Form.Input icon='user' iconPosition='left' placeholder='Username' style={{ padding: 5, textAlign: 'center' }} />
                                  <br />
                                  <Button icon='mail' color="black" padding="10" style={{ marginBottom: '30px' }}>Send Mail</Button>
                                </Modal.Content>
                              </center>
                            </Modal>
                            <Button negative>Delete</Button>
                          </Table.Cell>
                        </Table.Row>

                      </Table.Body>

                      
                    </Table>
                  </Container>
                </Grid.Column>
                <Grid.Column style={{ float: 'right', width: '30%' }}>
                  <Container style={{}}>
                    <Header as='h2' color='black'>
                      Register Voter
                       </Header>
                    <Card style={{ width: '100%' }}>
                      <br/>
                      <Form.Group size='large' style={{ marginLeft: '15%', marginRight: '15%' }} >
                        <Form.Input
						style={{marginTop: '10px'}}
                          fluid
                          id='register_voter_email'
                          label='Email:'
                          placeholder='Enter your email.'
                          textAlign='center'
                        />

                        <br /><br />
                        <Button primary style={{ Bottom: '10px', marginBottom: '15px' }} onClick={this.register}>Register</Button>
                      </Form.Group>
                    </Card>
                  </Container>
                </Grid.Column>
              </Grid.Column>
            </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}


export default VotingList
