import React, { Component } from 'react';
import { Grid, Step, Icon, Menu, Sidebar, Container, Card, Button } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import { Bar } from 'react-chartjs-2';
import 'chartjs-plugin-annotation';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';
import web3 from '../../Ethereum/web3';

const options = {
  maintainAspectRatio: true,
  scales: {
    yAxes: [{
      height: '500px',
      stacked: true,
      gridLines: {
        display: true,
        color: "rgba(255,99,132,0.2)"
      }
    }],
    xAxes: [{
      width: '500px',
      gridLines: {
        display: false
      }
    }]
  }
};

const data = {
  labels: ["Mary", "Peter", "Susan", "Tom", "Bob", "John", "Jake"],
  datasets: [{
    label: "Vote Counts",
    backgroundColor: "rgba(255,99,132,0.2)",
    borderColor: "rgba(255,99,132,1)",
    borderWidth: 2,
    hoverBackgroundColor: "rgba(255,99,132,0.4)",
    hoverBorderColor: "rgba(255,99,132,1)",
    data: [65, 59, 20, 81, 56, 55, 40],
  }]
};

class ContainerExampleContainer extends Component {

    state = {
        election_name: '',
        election_desc: ''
    };
  async componentDidMount() {
      const add = Cookies.get('address');
      const election = Election(add);
      const summary = await election.methods.getElectionDetails().call();
      console.table(summary);
      this.getElectionDetails();
      this.setState({
          election_name: summary[0],
          election_desc: summary[1]
      });
  }  

  getElectionDetails = () => {
      /* const {
          election_name,
          election_desc
      } = this.state; */

      const item = [
          {
            header: 'Election Name',
            description: election_name  
          },
          {
            header: 'Election Descrciption',
            description: election_desc
          },
      ];
      return (<Card.Group items={item}/>)
  }

  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
      <Menu.Item as='a' style={{ color: 'grey' }} >
      <h2>MENU</h2><hr/>
      </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='user outline' />
          Candidate List
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='list' />
          Voter List
        </Menu.Item>
        <hr/>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='sign out' />
          Sign Out
        </Menu.Item>         
      </Sidebar>
    </Sidebar.Pushable>
  )
  CardExampleGroupProps = () => (<Card.Group >

  </Card.Group>)

    returnGraph = () => (
      <div>      
        
      <Bar
        data={data}
        width={100}
        height={50}
        options={options}
      />
    
    </div>
    )
    
  render() {
    return (  
      <div>                
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>

            <Layout>              
              <Grid.Column width={16}>
                <Step.Group style={{ minWidth: 1130, minHeight: 90, }}>
                  <Step icon='users' title='Voters' description='10' />
                  <Step icon='user outline' title='Candidates' description='2' />
                  <Step icon='chart bar outline' title='Total Votes' description='23' />
                </Step.Group>
                {this.CardExampleGroupProps()}
                <Grid.Column style={{ maxHeight: '500px' }}>
                  <Container>
                    <br></br>
                          {this.getElectionDetails()}                                                             
                  </Container>
                </Grid.Column>
                <Grid.Column>
                  <div className="he">
                  <style jsx>{`
                    .he {
                      height: 50%;
                      max-width: 100%;
                    }
                  `}</style>
                  {this.returnGraph()}
                  </div> 
                </Grid.Column>                
              </Grid.Column>            
              </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default ContainerExampleContainer;
