import React,{Component} from 'react'
import { Button, Form, Grid, Header, Segment, Icon} from 'semantic-ui-react'
import web3 from '../../Ethereum/web3';
import Election_Factory from '../../Ethereum/election_factory'
import {Router} from '../../routes';
class LoginForm extends Component {
 
    state = {
        //retrieve the company's email via cookie
        election_name: '',
        election_description: ''
    }

    signin = async event => {
        try {
        console.log(this.state.election_name);
        console.log(this.state.election_description);
        const accounts = await web3.eth.getAccounts();
        console.log(accounts[0]);
        await Election_Factory.methods
            .createElection("abc@abc.co",this.state.election_name,this.state.election_description)
            .send({from: accounts[0]});
        console.log(address);
        alert("Election created successfully!");
        //console.log(address);
        //Router.pushRoute(`/election/${address}/company_dashboard`);
        }catch(err) {
            console.log(err.Message);
        }
    }

    LoginForm = () => (         
        <div className='login-form'>          
          <style JSX>{`
                .login-form {
                    width:100%;
                    height:100%;
                    position:absolute;
                    background: url('../../static/blockchain.jpg') no-repeat;
                } 
              `}</style>
          
          <Grid textAlign='center' style={{ height: '100%'}} verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 380 }}>              
              <Form size='large'>        
                <Segment>
                <Header as='h2' color='black' textAlign='center' style={{marginTop: 10}}>                 
                Create an election!
              </Header>
                  <Form.Input fluid iconPosition='left' icon="address card outline" placeholder='Election Name' 
                  style={{padding: 5}}
                  value={this.state.election_name}
                  onChange={event => this.setState({election_name: event.target.value })}
                  required={true}
                  />
                  <Form.Input
                    as='TextArea'
                    required={true}
                    style={{maxHeight: '30px', maxWidth:'96%', marginBottom: '10px'}}
                    fluid            
                    placeholder= 'Election Description'                      
                    value={this.state.election_description}
                    onChange={event => this.setState({election_description: event.target.value })}
                  />
      
                  <Button color='blue' fluid size='large' style={{marginBottom: 15}} onClick={this.signin}>
                    Submit
                  </Button>
                </Segment>
              </Form>              
            </Grid.Column>
          </Grid>          
        </div>
      )

render() {
   
    return (
      <div>
    <link
           rel="stylesheet"
           href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
        />
      {/* <link href="../css/paper-dashboard.css?v=2.0.0" rel="stylesheet" /> */}
      {this.LoginForm()}
      
      </div>
  );
  }
}

    export default LoginForm
