import React, { Component } from 'react';
import { Grid, Table, Button, Form, Image, Input,Header, Icon, Menu, Modal, Sidebar, Container, Card } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import Cookies from 'js-cookie';
import {Router} from '../../routes';
import Election from '../../Ethereum/election';
import ipfs from '../../ipfs';
class VotingList extends Component { 

    state = {
        election_address: Cookies.get('address'),
        election_name: '',
        election_description: '',
        candidates: [],
        cand_name: '',
        cand_desc: '',
        buffer: '',
        ipfsHash: null,
        loading: false
    }

    async componentDidMount() {
        try {
            const add = Cookies.get('address');
            const election = Election(add);
            const summary = await election.methods.getElectionDetails().call();
            this.setState({
                election_name: summary[0],
                election_description: summary[1]
            });            
            const c = await election.methods.getNumOfCandidates.call();
            console.log("NumCand: "+c);
            let candidates = [];
            for(let i=0 ; i<c; i++) {
                candidates.push(await election.methods.getCandidate(i).call());
            }
        let i=-1;
        const items = candidates.map(candidate => {
            i++;
            return {
              header: candidate[0],
              description: candidate[1],
              image: (
                  <Image src={`https://ipfs.io/ipfs/${candidate[2]}`} size='medium'/>
                ),
              extra: (
                  <div>
                    <Icon name='pie graph' iconPostion='left'/>  
                    {candidate[3].toString()}  
                </div>
              ) 
            };
        });
        this.setState({item: items}); 
        } catch(err) {
            console.log(err.message);
            alert("Redirecting you to login page...");
            Router.pushRoute('/company_login');
        }
    }
    getElectionDetails = () => {
        const {
            election_name,
            election_description
        } = this.state;
    
        return (
          <div style={{marginLeft: '45%',marginBottom: '2%',marginTop: '2%'}}>
            <Header as="h2">
              <Icon name="address card" />
              <Header.Content>
                {election_name}
                <Header.Subheader>{election_description}</Header.Subheader>
              </Header.Content>
            </Header>
          </div>
        );
      }

    renderTable = () => {
        return (<Card.Group items={this.state.item}/>)
    } 

    captureFile = (event) => {
        event.stopPropagation()
        event.preventDefault()
        const file = event.target.files[0]
        let reader = new window.FileReader()
        reader.readAsArrayBuffer(file)
        reader.onloadend = () => this.convertToBuffer(reader)
    };
    
    convertToBuffer = async(reader) => {
        //file is converted to a buffer for upload to IPFS
        const buffer = await Buffer.from(reader.result);
        //set this buffer -using es6 syntax
        this.setState({buffer});
        console.log(buffer);
    };
    
    onSubmit = async (event) => {
        event.preventDefault();
        this.setState({loading: true});
        const accounts = await web3.eth.getAccounts();
    
        try {
        await ipfs.add(this.state.buffer, (err, ipfsHash) => {
            console.log(err,ipfsHash);
            this.setState({ ipfsHash: ipfsHash[0].hash });
    
            this.state.election_address.methods.addCandidate(this.state.cand_name,this.state.cand_desc,this.state.ipfsHash,/* */).send({
                from: accounts[0]}, (error, transactionHash) => {}
            );       
        })
        alert("Added!");
        this.setState({loading: false});
        } catch (err) {
            alert("Error in file processing.");
        }
        //ajax script below
        const email = document.getElementById('email').value;
        console.log(email);
            var http = new XMLHttpRequest();
            var url = "/candidate/registerCandidate";
            var params = "email=" + email+ "&election_name=" + this.state.election_name;
            http.open("POST", url, true);
            //Send the proper header information along with the request
            http.setRequestHeader(
                "Content-type",
                "application/x-www-form-urlencoded"
            );
            http.onreadystatechange = function() {
                //Call a function when the state changes.
                if (http.readyState == 4 && http.status == 200) {
                    var responseObj = JSON.parse(http.responseText);
                    if(responseObj.status=="success") {
                      alert(responseObj.message);
                      //window.location("localhost:3000");
                    }
                    else {
                      alert(responseObj.message);
                    }
                }
            };
            http.send(params);
    
    };
    

  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (

    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='user outline' />
          Candidate List
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='list' />
          Voter List
          </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='sign out' />
          Sign Out
          </Menu.Item>

      </Sidebar>
    </Sidebar.Pushable>
  )

    register = event => {

		const email = document.getElementById('register_voter_email').value;        
    
		var http = new XMLHttpRequest();
        var url = "/candidate/register";
        var params = "email=" + email+"&election_address=" + this.state.election_address+ "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {

                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") 
                    alert(responseObj.message);                  
                else 
                    alert(responseObj.message);                
            }
        };
    	http.send(params);
	}
	
	mail_to_all = event => {		
		var http = new XMLHttpRequest();
        var url = "voter/sendMail";
        var params = "election_address=" + this.state.election_address + "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
            "Content-type",
            "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
            //Call a function when the state changes.
            if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if(responseObj.status=="success") {
                  alert(responseObj.message);                  
                }
                else {
                  alert(responseObj.message);
                }
            }
        };
    	http.send(params);
	}

  renderRows() {
      return(
          this.state
      )
  }

  render() {
      const {Body, Row, HeaderCell, Header} = Table;
    return (
      <div>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Layout>
                {this.getElectionDetails()}                      
              <br />
              <br />
              <Grid.Column width={14} style={{ minHeight: '630px' }}>
                <Grid.Column style={{ float: 'left', width: '60%' }}>
                  <Header as='h2' color='black'>
                    Candidate List
              </Header>
                  <Container>                      
                      <table>
                      {this.renderTable()}
                      </table>                                        
                  </Container>
                </Grid.Column>
                <Grid.Column style={{ float: 'right', width: '30%' }}>
                <Container style={{marginLeft:'50px'}}>                      
                <Header as='h2' color='black' textAlign='center'>                 
                        Add Candidate
                       </Header>
                       <Card style={{width: '100%'}}>      
                       
                       <Form.Group size='large'style={{marginLeft: '15%',marginRight: '15%'}} >                       
                       <br/>
                       <Form.Input
                        fluid
                        label='Name:'
                        placeholder='Enter your name.'
                        onChange={event => this.setState({ cand_name: event.target.value })}
                        textAlign='center'
                       
                    />        
                        
                        <p>Image:</p>
                       
                        
                        <div class="ui fluid" style={{ borderWidth: '0px', marginRight: '20%' }}>
                          <input type="file" class="inputfile" id="embedpollfileinput"                           
                            onChange={this.captureFile}
                            style={{ maxWidth: '0.1px', maxHeight: '0.1px', zIndex: '-1', overflow: 'hidden', position: 'absolute' }} 
                          />
                          <label for="embedpollfileinput" class="ui huge blue right floated button" style={{ fontSize: '15px', marginRight: '30%' }}>
                            <i class="ui upload icon"></i>
                            Upload image
                          </label>
                        </div><br /><br /><br />
                        <p>Description:</p>
                        <Form.Input as='TextArea'
                         fluid
                         label='Description:'                         
                         placeholder='Describe here.'
                         style={{width: '100%'}}
                         centered={true}
                         onChange={event => this.setState({ cand_desc: event.target.value })}
                          />
                       <br/><br/>
                       <p>E-mail ID: </p>
                       <Form.Input fluid
                         id="email"
                         placeholder="Enter your e-mail"
                       />
                       <br/>
                       <Button primary onClick={this.onSubmit} loading={this.state.loading} style={{Bottom: '10px',marginBottom: '10px'}}>Register</Button>
                        </Form.Group>                                  
                      </Card> 
                    </Container>
                </Grid.Column>                
              </Grid.Column>
            </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}


export default VotingList
