import React, { Component } from 'react';
import { Grid, Step, Icon, Menu, Sidebar, Container, Card, Header, Button, Item } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import { Bar } from 'react-chartjs-2';
import 'chartjs-plugin-annotation';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';
import web3 from '../../Ethereum/web3';
import {Link,Router} from '../../routes';

let graphEmail = ["ABC","BCD"];
let graphVotes = ["2","0"];
const options = {
  maintainAspectRatio: true,
  responsive: true,
  scales: {
    yAxes: [{
      height: '500px',
      stacked: true,
      gridLines: {
        display: true,
        color: "rgba(255,99,132,0.2)"
      }
    }],
    xAxes: [{
      width: '500px',
      gridLines: {
        display: false
      }
    }]
  }
};

const data = {
  labels: graphEmail,
  datasets: [{
    label: "Vote Counts",
    backgroundColor: "rgba(255,99,132,0.2)",
    borderColor: "rgba(255,99,132,1)",
    borderWidth: 2,
    hoverBackgroundColor: "rgba(255,99,132,0.4)",
    hoverBorderColor: "rgba(255,99,132,1)",
    data: graphVotes,
  }]
};

class ContainerExampleContainer extends Component {

    state = {
        election_address: Cookies.get('address'),
        election_name: '',
        election_desc: '',
        voters: 0,
        candidates: 0,
        visible: false,
        loading: false
    };
  async componentDidMount() {
    document.title="Dashboard";  
    var http = new XMLHttpRequest();
    var url = '/voter/';
    var params = 'election_address='+Cookies.get('address');    
    http.open("POST", url, true);
    var cnt = [];
    //Send the proper header information along with the request
    http.setRequestHeader(
        "Content-type",
        "application/x-www-form-urlencoded"
    );
    http.onreadystatechange = function() {
        //Call a function when the state changes.
        if (http.readyState == 4 && http.status == 200) {
            var responseObj = JSON.parse(http.responseText);
            if(responseObj.status=="success") {
               console.log(responseObj.count); 
               cnt.push(responseObj.count);
            }                
        }        
    };
    http.send(params);
    console.log("Count: "+cnt);
      try {
      const add = Cookies.get('address');
      const election = Election(add);
      const summary = await election.methods.getElectionDetails().call();
      const v = (await election.methods.getNumOfVoters().call());
      console.log("NumVoters: "+v);  
      this.setState({voters: v});      
      const c = (await election.methods.getNumOfCandidates().call());
      console.log("NumCand: "+c);
      this.setState({candidates: c})
      this.setState({
        election_name: summary[0],
        election_desc: summary[1]
    });
            
    let candidates = [];
    for(let i=0 ; i<c; i++) 
        candidates.push(await election.methods.getCandidate(i).call());

    graphEmail = candidates.map(candidate => { return candidate[0] });
    graphVotes = candidates.map(candidate => { return candidate[3] });

    console.log(graphEmail);
    console.log(graphVotes);
            
    }catch (err) {
        console.log(err.message);
        alert("Redirecting you to login page...");
        Router.pushRoute('/company_login');
    }
  }  

  getElectionDetails = () => {
      const {
          election_name,
          election_desc
      } = this.state;
      
      return (
        <div style={{marginLeft: '43%',marginBottom: '2%',marginTop: '2%',float:'left'}}>
          <Header as="h2">
            <Icon name="address card" />
            <Header.Content>
              {election_name}
              <Header.Subheader>{election_desc}</Header.Subheader>
            </Header.Content>
          </Header>
        </div>
      );
  }
  CardExampleGroupProps = () => (<Card.Group></Card.Group>)
  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
      <Menu.Item as='a' style={{ color: 'grey' }} >
      <h2>MENU</h2><hr/>
      </Menu.Item>      
        <Menu.Item style={{ color: 'grey', fontColor: 'grey' }}>
          <Icon name='dashboard'/>
          Dashboard
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='user outline' />
          Candidate List
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='list' />
          Voter List
        </Menu.Item>
        <hr/>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='sign out' />
          Sign Out
        </Menu.Item>         
      </Sidebar>
    </Sidebar.Pushable>
  )
  clickDash = () => {
    Router.pushRoute(`/election/${Cookies.get('address')}/company_dashboard`);
  }
  endElection = async event => {
    try {
        this.setState({loading: true});        
        const add = Cookies.get('address');
        console.log(add);
        const election = Election(add);
        console.log(election);
        const accounts = await web3.eth.getAccounts();
        const num = await election.methods.getNumOfCandidates().call();
        console.log(num);
        const candidate = await election.methods.WinnerCandidate().send({from: accounts[0]});
        console.log(candidate);
    } catch(err) {
        console.log(err.message);
    }
    /* console.log(candidate);
    var http = new XMLHttpRequest();
        var url = "/voter/resultMail";
        var params = 'election_address='+Cookies.get('address')+'&election_name='+this.state.election_name+'&candidate_email='+candidate[1]+'&winner_candidate='+candidate[0];
        http.open("POST", url, true);
        //Send the proper header information along with the request
        http.setRequestHeader(
        "Content-type",
        "application/x-www-form-urlencoded"
        );
        http.onreadystatechange = function() {
        //Call a function when the state changes.
        if (http.readyState == 4 && http.status == 200) {
            var responseObj = JSON.parse(http.responseText);
            if(responseObj.status=="success") {
                alert("Mail sent!");
            }
            else {
            alert(responseObj.message);
            }
            
        }  
    };
    http.send(params);  
    this.setState({loading: true}); */
  }

    returnGraph = () => { 
      return (<Bar data={data} width={120} height={50} options={options}/>); 
    }
    
  render() {
    return (  
      <div>                
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>

            <Layout>              
              <Grid.Column width={16}>
              {this.getElectionDetails()}           
              <Button negative style={{float: 'right',marginTop: '2%'}} onClick={this.endElection} loading={this.state.loading}>End election</Button>
                <Step.Group style={{ minWidth: 1130, minHeight: 90, }}>
                  <Step icon='users' title='Voters' description={this.state.voters} />
                  <Step icon='user outline' title='Candidates' description={this.state.candidates}/>
                  <Step icon='chart bar outline' title='Total Votes' description={this.state.voters} />
                </Step.Group>
                {this.CardExampleGroupProps()}
                
                <Grid.Column visible={this.state.visible}>
                    <br/>
                  <div className="he">
                  <style jsx>{`
                    .he {
                      height: 50%;
                      max-width: 100%;
                    }
                  `}</style>
                  {this.returnGraph()}
                  </div> 
                </Grid.Column>                
              </Grid.Column>            
              </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default ContainerExampleContainer;
