import React, { Component } from 'react';
import { Grid, Button, Form, Header, Icon, Menu, Sidebar, Container, Card } from 'semantic-ui-react';
import Layout from '../../components/Layout'; 
import web3 from '../../Ethereum/web3';
import Election from '../../Ethereum/election';
import ipfs from '../../ipfs';
import Cookies from 'js-cookie';
import {Router} from '../../routes';
class VotingList extends Component {

  state = {
     election_name: '',
     elction_desc: '',
     buffer: '',
     ipfsHash: ''
  };

  async componentDidMount() {
    try {
        const add = Cookies.get('address');
        const election = Election(add);
        const summary = await election.methods.getElectionDetails.call();
        this.setState({
            election_name: summary[0],
            election_desc: summary[1]
        });
        this.getElectionDetails();
    } catch(err) {
        console.log(err.message);
        alert("Redirecting you to login page!");
        Router.pushRoute('/company_login');
    }
  }
  getElectionDetails = () => {
    const {
        election_name,
        election_desc
    } = this.state;

    return (
      <div style={{marginLeft: '45%',marginBottom: '2%',marginTop: '2%'}}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_desc}</Header.Subheader>
          </Header.Content>
        </Header>
      </div>
    );
  }
    
  captureFile = (event) => {
    event.stopPropagation()
    event.preventDefault()
    const file = event.target.files[0]
    let reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => this.convertToBuffer(reader)
};

convertToBuffer = async(reader) => {
    //file is converted to a buffer for upload to IPFS
    const buffer = await Buffer.from(reader.result);
    //set this buffer -using es6 syntax
    this.setState({buffer});
    console.log(buffer);
};

onSubmit = async (event) => {
    event.preventDefault();

    console.log("web3 value is ",web3.eth.getAccounts());
    const accounts = await web3.eth.getAccounts();
    console.log('Sending from Metamask account: ' , accounts[0]);

    await ipfs.add(this.state.buffer, (err, ipfsHash) => {
        console.log(err,ipfsHash);
        this.setState({ ipfsHash: ipfsHash[0].hash });

        election.methods.addCandidate(cand_name,cand_desc,this.state.ipfsHash).send({
            from: accounts[0]}, (error, transactionHash) => {}
        );
    })
};

    GridExampleGrid = () => <Grid>{columns}</Grid>
    SidebarExampleVisible = () => (
  
      <Sidebar.Pushable>
        <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
        <h2>MENU</h2><hr/>
        </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }} >
            <Icon name='dashboard' />
            Dashboard
          </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='user outline' />
            Candidate List
          </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='list' />
            Voter List
          </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
 
        </Sidebar>
      </Sidebar.Pushable>
    )
  
    render() {
      return (
        <div> 
          <Grid>
            <Grid.Row>
              <Grid.Column width={2}>
                {this.SidebarExampleVisible()}
              </Grid.Column>
              <Layout>       
                  {this.getElectionDetails()}                            
                <Grid.Column width={16} style={{ minHeight: '70vh', width: '50%', marginLeft: '30%'}}>
                    <Container style={{marginTop:'80px',marginLeft:'50px'}}>                      
                       <Card style={{width: '80%'}}>      
                       
                       <Form.Group  size='large'style={{marginLeft: '15%',marginRight: '15%'}} >
                       <Header as='h2' color='black' textAlign='center' style={{marginTop: 10}}>                 
                        Add Candidate
                       </Header>
                       <Form.Input
                        fluid
                        label='Name:'
                        placeholder='Enter your name.'
                        
                        textAlign='center'
                       
                    />        
                        
                        <p>Image:</p>
                       
                        
                        <div class="ui fluid" style={{ borderWidth: '0px', marginRight: '20%' }}>
                          <input type="file" class="inputfile" id="embedpollfileinput" style={{ maxWidth: '0.1px', maxHeight: '0.1px', zIndex: '-1', overflow: 'hidden', position: 'absolute' }} />
                          <label for="embedpollfileinput" class="ui huge blue right floated button" style={{ fontSize: '15px', marginRight: '30%' }}>
                            <i class="ui upload icon"></i>
                            Upload image
                          </label>
                        </div><br /><br /><br />
                        <p>Description:</p>
                        <Form.Input as='TextArea'
                         fluid
                         label='Description:'                         
                         placeholder='Describe here.'
                         style={{width: '100%'}}
                         centered={true}
                          />
                       <br/><br/>
                       <Button primary style={{Bottom: '10px',marginBottom: '10px'}}>Register</Button>
                        </Form.Group>          
                      </Card> 
                    </Container>
                  </Grid.Column>                
              </Layout>
            </Grid.Row>
          </Grid>
        </div>
      );
    }
  }
  

export default VotingList
