import React,{Component} from 'react';
import Layout from '../../components/Layout'
import {Form, Button, Input, Message} from 'semantic-ui-react';
import factory from '../../Ethereum/factory'; //deployed instance of Factory
import web3 from '../../Ethereum/web3';
import {Router} from '../../routes'; 

class NewCampaign extends Component {   
    state = {
        minimumContribution: '',
        errorMess: '',
        loading: false  
    };

    onSubmit = async event => {
        event.preventDefault();
        this.setState({loading: true, /*This is for  */errorMess: ''});

        try {
            const accounts = await web3.eth.getAccounts();
            await factory.methods.createCampaign(this.state.minimumContribution)
                .send({ from: accounts[0] });
                
            Router.pushRoute('/');
        } 
        catch(err) {
            this.setState({ errorMess: err.message });
        }

        this.setState({loading: false});
    };

    render() {
        return(
            <Layout>
                <h3>Create a New Campaign</h3>

                <Form onSubmit={this.onSubmit} error={!!this.state.errorMess}>
                    <Form.Field>
                        <label>Minimum Contribution</label>
                        <Input 
                            label="wei" 
                            labelPosition="right" 
                            value={this.state.minimumContribution} 
                            onChange={event => this.setState({minimumContribution: event.target.value })}
                        />
                    </Form.Field>
                    
                    <Message error header='Oops!' content={this.state.errorMess} />
                    <Button primary loading={this.state.loading}>Create!</Button>
                </Form>
            </Layout>
        );
    }
}

export default NewCampaign;
