import React, { Component } from 'react';
import { Grid, Step, Button, Checkbox, Form, Header, Icon, Image, Menu, Segment, Sidebar,TextArea, Container, Card } from 'semantic-ui-react';
import Layout from '../../components/Layout'; 
import web3 from '../../Ethereum/web3';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';

class VotingList extends Component {

    state = {
        election_name: '',
        election_desc: ''
    };
    GridExampleGrid = () => <Grid>{columns}</Grid>
    SidebarExampleVisible = () => (
  
      <Sidebar.Pushable>
        <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
        <h2>MENU</h2><hr/>
        </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }} >
            <Icon name='dashboard' />
            Dashboard
          </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='user outline' />
            Candidate List
          </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='list' />
            Voter List
          </Menu.Item>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
 
        </Sidebar>
      </Sidebar.Pushable>
    )
    async componentDidMount() {
        try {
            const add = Cookies.get('address');
            const election = Election(add);
            const summary = await election.methods.getElectionDetails.call();
            this.setState({
                electiondet: election,
                election_name: summary[0],
                election_desc: summary[1]
            });
            this.getElectionDetails();
        } catch(err) {
            console.log(err.message);
            alert("Redirecting you to login page!");
            Router.pushRoute('/company_login');
        }
      }
      getElectionDetails = () => {
        const {
            election_name,
            election_desc
        } = this.state;
    
        return (
          <div style={{marginLeft: '45%',marginBottom: '2%',marginTop: '2%'}}>
            <Header as="h2">
              <Icon name="address card" />
              <Header.Content>
                {election_name}
                <Header.Subheader>{election_desc}</Header.Subheader>
              </Header.Content>
            </Header>
          </div>
        );
      }
  
  
    render() {
      return (
        <div> 
          <Grid>
            <Grid.Row>
              <Grid.Column width={2}>
                {this.SidebarExampleVisible()}
              </Grid.Column>
              <Layout>                                   
              {this.getElectionDetails()}
              <Grid.Column style={{minHeight: '99%'}}>
                    <h1>Hello</h1>    
              </Grid.Column>      
              </Layout>
            </Grid.Row>
          </Grid>
        </div>
      );
    }
  }
  

export default VotingList
