import React, {Component} from 'react';
import Layout from '../../components/Layout';
import Campaign from '../../Ethereum/campaign';
import {Card} from 'semantic-ui-react';

class ShowCampaign extends Component {
    static async getInititalProps(props) { 
       const campaign = Campaign(props.query.address);
       console.log("abbcc"); 
       const summary = await campaign.methods.getSummary().call();

       return{
           minCont: summary[0],
           balance: summary[1],
           requestsCount: summary[2],
           approversCount: summary[3],
           manager: summary[4]
       };
    }

    renderCards() {
        const {
            balance,
            manager,
            minCont,
            requestsCount,
            approversCount
        } = this.props;

        console.log(summary);

        const items = [
          {
            header: manager,
            description: 'Manager created the campaign; Can create requests',
            meta: 'Address of Manager',
          },
          {
            header: balance,
            description: 'Amount donated in the campaign',
            meta: 'Balance',
          },
          {
            header: minCont,
            description: 'Mininum Contribution required to contribute',
            meta: 'Mininum Contribution'  
          },
          {
            header: requestsCount,
            description: 'Number of contributors for the campaign',
            meta: 'No. of Contributors',  
          },
          {
            header: approversCount,
            description: 'Number of approvers for the campaign',
            meta: 'No. of Approvers',  
          }
        ];

        return <Card.Group items={items} />
    }
    
    render() {
        return (
            <Layout>
                <h3>Campaign Details</h3>
                {this.renderCards()}
            </Layout>
        );
    }
}

export default ShowCampaign;
