import React,{Component} from 'react'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'
import Layout from '../components/Layout';
/* import Background from 'blockchain.jpg'; */
class LoginForm extends Component {
 
    LoginForm = () => (         
        <div className='login-form'>          
          <style JSX>{`
                .login-form {
                    width:100%;
                    height:100%;
                    position:absolute;
                    background: url('/static/blockchain.jpg') no-repeat;
                } 
              `}</style>
          
          <Grid textAlign='center' style={{ height: '100%'}} verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 380 }}>              
              <Form size='large'>        
                <Segment>
                <Header as='h2' color='black' textAlign='center' style={{marginTop: 10}}>                 
                Login
              </Header>
                  <Form.Input fluid icon='user' iconPosition='left' placeholder='Username' style={{padding: 5}} />
                  <Form.Input
                    style={{padding: 5}}
                    fluid
                    icon='lock'
                    iconPosition='left'
                    placeholder='Password'
                    type='password'
                  />
      
                  <Button color='blue' fluid size='large' style={{marginBottom: 15}} onClick={this.signin}>
                    
                    Submit
                  </Button>
                </Segment>
              </Form>              
            </Grid.Column>
          </Grid>          
        </div>
      )
    signin = event => {
        
    }

render() {
   
    return (
      <div>
    <link
           rel="stylesheet"
           href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
        />
      {/* <link href="../css/paper-dashboard.css?v=2.0.0" rel="stylesheet" /> */}
      {this.LoginForm()}
      
      </div>
  );
  }
}

    export default LoginForm
