import React, { Component } from 'react';
import { Grid, Step, Icon, Menu, Sidebar, Container, Card, Header, Button, Item } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import { Bar } from 'react-chartjs-2';
import 'chartjs-plugin-annotation';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';
import web3 from '../../Ethereum/web3';
import {Router} from '../../routes';

const options = {
  maintainAspectRatio: true,
  scales: {
    yAxes: [{
      height: '500px',
      stacked: true,
      gridLines: {
        display: true,
        color: "rgba(255,99,132,0.2)"
      }
    }],
    xAxes: [{
      width: '500px',
      gridLines: {
        display: false
      }
    }]
  }
};

const data = {
  labels:[Item],
  datasets: [{
    label: "Vote Counts",
    backgroundColor: "rgba(255,99,132,0.2)",
    borderColor: "rgba(255,99,132,1)",
    borderWidth: 2,
    hoverBackgroundColor: "rgba(255,99,132,0.4)",
    hoverBorderColor: "rgba(255,99,132,1)",
    data: [],
  }]
};

class ContainerExampleContainer extends Component {

    state = {
        election_name: '',
        election_desc: '',
        voters: 0,
        candidates: 0,
        visible: false
    };
  async componentDidMount() {
      try {
      const add = Cookies.get('address');
      const election = Election(add);
      const summary = await election.methods.getElectionDetails().call();
      const v = (await election.methods.getNumOfVoters().call()).toString(1);
      console.log(v);  
      this.setState({voters: v});      
      const c = (await election.methods.getNumOfCandidates().call()).toString(1);
      console.log(c);
      this.setState({candidates: c})
      this.getElectionDetails();
      this.setState({
          election_name: summary[0],
          election_desc: summary[1]
      });
      const candidates = await election.methods.getCandidate(0).call();
      console.log(candidates);
    }catch (err) {
        console.log(err.message);
        alert("Redirecting you to login page...");
        Router.pushRoute('/company_login');
    }
  }  

  getElectionDetails = () => {
      const {
          election_name,
          election_desc
      } = this.state;

      return (
        <div style={{marginLeft: '43%',marginBottom: '2%',marginTop: '2%'}}>
          <Header as="h2">
            <Icon name="address card" />
            <Header.Content>
              {election_name}
              <Header.Subheader>{election_desc}</Header.Subheader>
            </Header.Content>
          </Header>
        </div>
      );
  }

  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
      <Menu.Item as='a' style={{ color: 'grey' }} >
      <h2>MENU</h2><hr/>
      </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='user outline' />
          Candidate List
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='list' />
          Voter List
        </Menu.Item>
        <hr/>
        <Menu.Item as='a' style={{ color: 'grey' }}>
          <Icon name='sign out' />
          Sign Out
        </Menu.Item>         
      </Sidebar>
    </Sidebar.Pushable>
  )
  CardExampleGroupProps = () => (<Card.Group >

  </Card.Group>)
  endElection = () => {
      return(
          <div>
              
          </div>
      )
  }

    returnGraph = () => (
      <Bar
        data={data}
        width={100}
        height={50}
        options={options}
      />    
    )
    
  render() {
    return (  
      <div>                
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>

            <Layout>              
              <Grid.Column width={16}>
              {this.getElectionDetails()}           
              {this.endElection()}
                <Step.Group style={{ minWidth: 1130, minHeight: 90, }}>
                  <Step icon='users' title='Voters' description={this.state.voters} />
                  <Step icon='user outline' title='Candidates' description={this.state.candidates}/>
                  <Step icon='chart bar outline' title='Total Votes' description={this.state.voters} />
                </Step.Group>
                {this.CardExampleGroupProps()}
                
                <Grid.Column visible={this.state.visible}>
                    <br/>
                  <div className="he">
                  <style jsx>{`
                    .he {
                      height: 50%;
                      max-width: 100%;
                    }
                  `}</style>
                  {this.returnGraph()}
                  </div> 
                </Grid.Column>                
              </Grid.Column>            
              </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default ContainerExampleContainer;
